NextLayer = "NextLayer"
FirstLayer = "FirstLayer"
ThisLayer = "ThisLayer"
Version = "GlManProServer-0.0"

class __private: # simple means to keep locals private
    import os.path
    import platform
    import sys
    import ctypes

    glmanInit = False

    # We use sys.platform here instead of platform.system() to avoid
    # forking a child process that might interfere with glman
    sysnm = sys.platform
    vers = platform.python_version().split('.')
    if vers[0] < '3' and vers[1] < '7':
        raise Exception("glman_for_python requires python 2.7 or newer")
    # prior to importing the python_bridge, we need to first ensure 
    # that libglman.so is loaded so all symbol resolution takes place.
    if sysnm == 'darwin':
        ext = ".dylib"
    elif sysnm == "win32":
        ext = ".dll"
    else:
        ext = ".so"
    thisfile = os.path.abspath(__file__)
    thisdir = os.path.dirname(thisfile)
    file = os.path.join(thisdir, "..", "libglman"+ext)
    libglman = ctypes.CDLL(file, ctypes.RTLD_GLOBAL)

import glman_for_python

def Init(args=[]):
    if not __private.glmanInit:
        __private.glmanInit = True
        try:
            import sys
            appname = sys.argv[0]
        except AttributeError:
            appname = 'unknownapp'
        glman_for_python.Init([appname] + args)

def Cleanup():
    glman_for_python.Cleanup()

def ParseFile(filename, layer=FirstLayer):
    glman_for_python.ParseFile(filename, layer)

