        import getpass
        self.ArchiveRecord(self.STRUCTURE, "RenderMan RIB-Structure 1.1")
        self.ArchiveRecord(self.STRUCTURE, "Creator glman_for_python " +                                             self.PRMAN_VERSION_STR)
        self.ArchiveRecord(self.STRUCTURE, "For " + getpass.getuser())
        self.ArchiveRecord(self.STRUCTURE, "Creation Date: " +                             time.ctime(time.time()))


